import "core-js/modules/es.array.push.js";
export default {
  name: "MyCheckbox",
  props: {
    options: {
      type: Array,
      require: true
    },
    name: {
      type: String,
      require: true
    }
  },
  data() {
    return {
      values: []
    };
  },
  methods: {
    updateValue: function (e) {
      if (e.target.checked) {
        this.values.push(e.target.value);
      } else {
        this.values = this.values.filter(v => v !== e.target.value);
      }
      this.$emit("input", this.values);
    }
  }
};
